import React, { useState, useEffect } from 'react';
import { Coffee, Plus, Search, Heart, Edit3, Trash2, Star, X, ChevronDown, ChevronUp, ExternalLink, Globe, Calendar, Percent } from 'lucide-react';

const CoffeeTracker = () => {
  const [coffees, setCoffees] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [expandedCoffee, setExpandedCoffee] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterFavorites, setFilterFavorites] = useState(false);
  const [editingCoffee, setEditingCoffee] = useState(null);

  const [formData, setFormData] = useState({
    roaster: '',
    description: '',
    origin: '',
    roastLevel: 'medium',
    favorite: false,
    grinded: false,
    grindingTime: '',
    grindingDegree: '',
    percentArabica: 100,
    percentRobusta: 0,
    cuppingTime: new Date(),
    cremaRating: 0,
    tasteRating: 0,
    tasteNotes: '',
    url: '',
    comment: ''
  });

  const roastLevels = ['light', 'light-medium', 'medium', 'medium-dark', 'dark'];

  const countryFlags = {
    'BR': { flag: 'ðŸ‡§ðŸ‡·', name: 'Brazil' },
    'IN': { flag: 'ðŸ‡®ðŸ‡³', name: 'India' },
    'ET': { flag: 'ðŸ‡ªðŸ‡¹', name: 'Ethiopia' },
    'NI': { flag: 'ðŸ‡³ðŸ‡®', name: 'Nicaragua' },
    'IT': { flag: 'ðŸ‡®ðŸ‡¹', name: 'Italy' },
    'GT': { flag: 'ðŸ‡¬ðŸ‡¹', name: 'Guatemala' },
    'UG': { flag: 'ðŸ‡ºðŸ‡¬', name: 'Uganda' },
    'CR': { flag: 'ðŸ‡¨ðŸ‡·', name: 'Costa Rica' },
    'CO': { flag: 'ðŸ‡¨ðŸ‡´', name: 'Colombia' },
    'SV': { flag: 'ðŸ‡¸ðŸ‡»', name: 'El Salvador' },
    'EC': { flag: 'ðŸ‡ªðŸ‡¨', name: 'Ecuador' },
    'MX': { flag: 'ðŸ‡²ðŸ‡½', name: 'Mexico' },
    'PE': { flag: 'ðŸ‡µðŸ‡ª', name: 'Peru' },
    'KE': { flag: 'ðŸ‡°ðŸ‡ª', name: 'Kenya' },
    'HN': { flag: 'ðŸ‡­ðŸ‡³', name: 'Honduras' },
    'VN': { flag: 'ðŸ‡»ðŸ‡³', name: 'Vietnam' },
    'ID': { flag: 'ðŸ‡®ðŸ‡©', name: 'Indonesia' },
    'JM': { flag: 'ðŸ‡¯ðŸ‡²', name: 'Jamaica' },
    'HI': { flag: 'ðŸŒº', name: 'Hawaii' },
    'PA': { flag: 'ðŸ‡µðŸ‡¦', name: 'Panama' },
    'SA': { flag: 'ðŸŒŽ', name: 'South America' }
  };

  useEffect(() => {
    const savedCoffees = localStorage.getItem('coffeeTracker');
    if (savedCoffees) {
      setCoffees(JSON.parse(savedCoffees));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('coffeeTracker', JSON.stringify(coffees));
  }, [coffees]);

  const resetForm = () => {
    setFormData({
      roaster: '',
      description: '',
      origin: '',
      roastLevel: 'medium',
      favorite: false,
      grinded: false,
      grindingTime: '',
      grindingDegree: '',
      percentArabica: 100,
      percentRobusta: 0,
      cuppingTime: new Date(),
      cremaRating: 0,
      tasteRating: 0,
      tasteNotes: '',
      url: '',
      comment: ''
    });
  };

  const handleSubmit = () => {
    if (!formData.roaster || !formData.description) {
      alert('Please enter at least roaster and description');
      return;
    }

    if (editingCoffee) {
      setCoffees(coffees.map(c => 
        c.id === editingCoffee.id 
          ? { ...formData, id: editingCoffee.id }
          : c
      ));
      setEditingCoffee(null);
    } else {
      const newCoffee = {
        ...formData,
        id: Date.now(),
        cuppingTime: new Date()
      };
      setCoffees([...coffees, newCoffee]);
    }

    setShowAddForm(false);
    resetForm();
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this coffee?')) {
      setCoffees(coffees.filter(c => c.id !== id));
    }
  };

  const handleEdit = (coffee) => {
    setFormData(coffee);
    setEditingCoffee(coffee);
    setShowAddForm(true);
    setExpandedCoffee(null);
  };

  const filteredCoffees = coffees.filter(coffee => {
    const matchesSearch = searchTerm === '' || 
      coffee.roaster.toLowerCase().includes(searchTerm.toLowerCase()) ||
      coffee.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
      coffee.tasteNotes?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesFavorite = !filterFavorites || coffee.favorite;

    return matchesSearch && matchesFavorite;
  });

  const StarRating = ({ rating, onRatingChange, readOnly = false }) => {
    return (
      <div className="flex space-x-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`w-5 h-5 cursor-pointer transition-colors ${
              star <= rating 
                ? 'text-yellow-400 fill-yellow-400' 
                : 'text-gray-300 hover:text-yellow-300'
            } ${readOnly ? 'cursor-default' : ''}`}
            onClick={() => !readOnly && onRatingChange && onRatingChange(star)}
          />
        ))}
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-amber-50 to-orange-100 p-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-2xl shadow-xl p-6 mb-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Coffee className="w-8 h-8 text-amber-600" />
              <h1 className="text-3xl font-bold text-gray-800">Coffee Tracker</h1>
            </div>
            <button
              onClick={() => {
                resetForm();
                setEditingCoffee(null);
                setShowAddForm(true);
              }}
              className="bg-amber-600 hover:bg-amber-700 text-white px-6 py-3 rounded-lg flex items-center space-x-2 transition-colors"
            >
              <Plus className="w-5 h-5" />
              <span>Add Coffee</span>
            </button>
          </div>

          {/* Stats */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-6">
            <div className="bg-amber-50 p-4 rounded-lg">
              <div className="text-2xl font-bold text-amber-600">{coffees.length}</div>
              <div className="text-gray-600">Total Coffees</div>
            </div>
            <div className="bg-amber-50 p-4 rounded-lg">
              <div className="text-2xl font-bold text-amber-600">
                {coffees.filter(c => c.favorite).length}
              </div>
              <div className="text-gray-600">Favorites</div>
            </div>
            <div className="bg-amber-50 p-4 rounded-lg">
              <div className="text-2xl font-bold text-amber-600">
                {coffees.length > 0 ? 
                  (coffees.reduce((sum, c) => sum + (c.tasteRating || 0), 0) / coffees.length).toFixed(1) 
                  : '0'
                }
              </div>
              <div className="text-gray-600">Avg. Rating</div>
            </div>
          </div>
        }
          {/* Search and Filter */}
          <div className="flex flex-col md:flex-row gap-4 mt-6">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
              <input
                type="text"
                placeholder="Search coffees..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
              />
            </div>
            <button
              onClick={() => setFilterFavorites(!filterFavorites)}
              className={`px-6 py-3 rounded-lg flex items-center space-x-2 transition-colors ${
                filterFavorites 
                  ? 'bg-amber-600 text-white' 
                  : 'bg-white border border-gray-300 text-gray-700 hover:bg-gray-50'
              }`}
            >
              <Heart className={`w-5 h-5 ${filterFavorites ? 'fill-white' : ''}`} />
              <span>Favorites</span>
            </button>
          </div>
        </div>

        {/* Add/Edit Form */}
        {showAddForm && (
          <div className="bg-white rounded-2xl shadow-xl p-6 mb-6">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-gray-800">
                {editingCoffee ? 'Edit Coffee' : 'Add New Coffee'}
              </h2>
              <button
                onClick={() => {
                  setShowAddForm(false);
                  setEditingCoffee(null);
                  resetForm();
                }}
                className="p-2 hover:bg-gray-100 rounded-lg"
              >
                <X className="w-5 h-5" />
              </button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Roaster *</label>
                <input
                  type="text"
                  value={formData.roaster}
                  onChange={(e) => setFormData({...formData, roaster: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  placeholder="Coffee Company"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Description *</label>
                <input
                  type="text"
                  value={formData.description}
                  onChange={(e) => setFormData({...formData, description: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  placeholder="Espresso Blend"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Origin</label>
                <input
                  type="text"
                  value={formData.origin}
                  onChange={(e) => setFormData({...formData, origin: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  placeholder="BR,CO,ET (use country codes)"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Roast Level</label>
                <select
                  value={formData.roastLevel}
                  onChange={(e) => setFormData({...formData, roastLevel: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                >
                  {roastLevels.map(level => (
                    <option key={level} value={level}>
                      {level.charAt(0).toUpperCase() + level.slice(1)}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Arabica %</label>
                <input
                  type="number"
                  min="0"
                  max="100"
                  value={formData.percentArabica}
                  onChange={(e) => {
                    const arabica = parseInt(e.target.value) || 0;
                    setFormData({
                      ...formData, 
                      percentArabica: arabica,
                      percentRobusta: 100 - arabica
                    });
                  }}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Robusta %</label>
                <input
                  type="number"
                  value={formData.percentRobusta}
                  disabled
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg bg-gray-100"
                />
              </div>

              <div className="flex items-center space-x-4">
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={formData.favorite}
                    onChange={(e) => setFormData({...formData, favorite: e.target.checked})}
                    className="w-4 h-4 text-amber-600 rounded focus:ring-amber-500"
                  />
                  <span className="text-sm font-medium text-gray-700">Favorite</span>
                </label>

                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={formData.grinded}
                    onChange={(e) => setFormData({...formData, grinded: e.target.checked})}
                    className="w-4 h-4 text-amber-600 rounded focus:ring-amber-500"
                  />
                  <span className="text-sm font-medium text-gray-700">Pre-ground</span>
                </label>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Grinding Degree</label>
                <input
                  type="text"
                  value={formData.grindingDegree}
                  onChange={(e) => setFormData({...formData, grindingDegree: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  placeholder="1-20"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Crema Rating</label>
                <StarRating 
                  rating={formData.cremaRating} 
                  onRatingChange={(rating) => setFormData({...formData, cremaRating: rating})}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Taste Notes</label>
                <input
                  type="text"
                  placeholder="erdig, schokoladig, nussig, fruchtig, sÃ¤uerlich, bitter, sÃ¼ÃŸ"
                  value={formData.tasteNotes}
                  onChange={(e) => setFormData({...formData, tasteNotes: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Product URL</label>
                <input
                  type="url"
                  placeholder="https://..."
                  value={formData.url}
                  onChange={(e) => setFormData({...formData, url: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Taste Rating</label>
                <StarRating 
                  rating={formData.tasteRating} 
                  onRatingChange={(rating) => setFormData({...formData, tasteRating: rating})}
                />
              </div>

              <div className="md:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-2">Comment</label>
                <textarea
                  value={formData.comment}
                  onChange={(e) => setFormData({...formData, comment: e.target.value})}
                  rows="3"
                  placeholder="Additional notes, brewing tips, personal thoughts..."
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                />
              </div>

              <div className="md:col-span-2 flex space-x-4">
                <button
                  onClick={handleSubmit}
                  className="bg-amber-600 hover:bg-amber-700 text-white px-6 py-2 rounded-lg transition-colors"
                >
                  {editingCoffee ? 'Update Coffee' : 'Add Coffee'}
                </button>
                <button
                  onClick={() => {
                    setShowAddForm(false);
                    setEditingCoffee(null);
                    resetForm();
                  }}
                  className="bg-gray-300 hover:bg-gray-400 text-gray-700 px-6 py-2 rounded-lg transition-colors"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
        {/* Coffee List */}
        <div className="grid gap-6">
          {filteredCoffees.map((coffee) => (
            <div key={coffee.id} className="bg-white rounded-2xl shadow-xl p-6 hover:shadow-2xl transition-shadow">
              <div className="flex justify-between items-start">
                <div className="flex-1">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-2">
                        <h3 className="text-xl font-bold text-gray-800">{coffee.roaster}</h3>
                        {coffee.favorite && <Heart className="w-5 h-5 text-red-500 fill-red-500" />}
                      </div>
                      <p className="text-lg text-gray-600 mb-2">{coffee.description}</p>

                      <div className="flex flex-wrap gap-2 mb-3">
                        {coffee.origin && coffee.origin.split(',').map((code, idx) => {
                          const country = countryFlags[code.trim()];
                          return country ? (
                            <span key={idx} className="inline-flex items-center space-x-1 bg-gray-100 px-2 py-1 rounded-full text-sm">
                              <span>{country.flag}</span>
                              <span>{country.name}</span>
                            </span>
                          ) : null;
                        })}

                        <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium
                          ${coffee.roastLevel === 'light' ? 'bg-yellow-100 text-yellow-800' : ''}
                          ${coffee.roastLevel === 'light-medium' ? 'bg-amber-100 text-amber-800' : ''}
                          ${coffee.roastLevel === 'medium' ? 'bg-orange-100 text-orange-800' : ''}
                          ${coffee.roastLevel === 'medium-dark' ? 'bg-orange-200 text-orange-900' : ''}
                          ${coffee.roastLevel === 'dark' ? 'bg-gray-800 text-white' : ''}
                        `}>
                          {coffee.roastLevel}
                        </span>

                        {coffee.grinded && (
                          <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-purple-100 text-purple-800">
                            Pre-ground
                          </span>
                        )}
                      </div>

                      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                        <div>
                          <span className="text-gray-500">Arabica:</span>
                          <span className="ml-2 font-medium">{coffee.percentArabica}%</span>
                        </div>
                        <div>
                          <span className="text-gray-500">Robusta:</span>
                          <span className="ml-2 font-medium">{coffee.percentRobusta}%</span>
                        </div>
                        <div>
                          <span className="text-gray-500">Crema:</span>
                          <div className="inline-flex ml-2">
                            <StarRating rating={coffee.cremaRating} readOnly />
                          </div>
                        </div>
                        <div>
                          <span className="text-gray-500">Taste:</span>
                          <div className="inline-flex ml-2">
                            <StarRating rating={coffee.tasteRating} readOnly />
                          </div>
                        </div>
                      </div>

                      {coffee.tasteNotes && (
                        <div className="mt-3">
                          <span className="text-gray-500 text-sm">Notes:</span>
                          <p className="text-gray-700">{coffee.tasteNotes}</p>
                        </div>
                      )}
                    </div>
                  </div>

                  <button
                    onClick={() => setExpandedCoffee(expandedCoffee === coffee.id ? null : coffee.id)}
                    className="mt-3 text-amber-600 hover:text-amber-700 text-sm font-medium flex items-center space-x-1"
                  >
                    {expandedCoffee === coffee.id ? (
                      <>
                        <ChevronUp className="w-4 h-4" />
                        <span>Show Less</span>
                      </>
                    ) : (
                      <>
                        <ChevronDown className="w-4 h-4" />
                        <span>Show More</span>
                      </>
                    )}
                  </button>

                  {expandedCoffee === coffee.id && (
                    <div className="mt-4 pt-4 border-t border-gray-200 space-y-2">
                      {coffee.grindingDegree && (
                        <div className="text-sm">
                          <span className="text-gray-500">Grinding Degree:</span>
                          <span className="ml-2 font-medium">{coffee.grindingDegree}</span>
                        </div>
                      )}
                      <div className="text-sm">
                        <span className="text-gray-500">Added:</span>
                        <span className="ml-2 font-medium">
                          {new Date(coffee.cuppingTime).toLocaleDateString()}
                        </span>
                      </div>
                      {coffee.url && (
                        <div className="text-sm">
                          <a 
                            href={coffee.url} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="text-sm text-blue-600 hover:text-blue-800 underline"
                          >
                            View Product Online â†’
                          </a>
                        </div>
                      )}
                      {coffee.comment && (
                        <p className="text-sm text-gray-600 italic">{coffee.comment}</p>
                      )}
                    </div>
                  )}
                </div>

                <div className="flex flex-col space-y-2 ml-4">
                  <button
                    onClick={() => handleEdit(coffee)}
                    className="p-2 text-gray-600 hover:text-amber-600 hover:bg-amber-50 rounded-lg transition-colors"
                  >
                    <Edit3 className="w-4 h-4" />
                  </button>
                  <button
                    onClick={() => handleDelete(coffee.id)}
                    className="p-2 text-gray-600 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {filteredCoffees.length === 0 && (
          <div className="bg-white rounded-2xl shadow-xl p-12 text-center">
            <Coffee className="w-16 h-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-gray-600 mb-2">No coffees found</h3>
            <p className="text-gray-500">
              {searchTerm || filterFavorites ? 'Try adjusting your search or filters' : 'Add your first coffee to get started!'}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CoffeeTracker;
